import React, { useState, useEffect } from 'react';
import { Button, Spin, Typography, Result, Form, Input, Card } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';
import './App.css';

const { Title } = Typography;

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [evaluated, setEvaluated] = useState(false);
  const [polling, setPolling] = useState(false);

  const simulateEventTrigger = () => {
    setLoading(true);
    setEvaluated(false);
    setPolling(true);

    setTimeout(() => {
      setEvaluated(true);
      setPolling(false);
      setLoading(false);
    }, 6000);
  };

  useEffect(() => {
    if (!polling) return;

    const interval = setInterval(() => {
      console.log('Polling backend...');
    }, 2000);

    return () => clearInterval(interval);
  }, [polling]);

  return (
    <div style={{ padding: '60px', textAlign: 'center' }}>
      <Title level={3}>Merchant Evaluation</Title>
      <Button type="primary" onClick={simulateEventTrigger} disabled={loading}>
        Trigger Evaluation
      </Button>

      <div style={{ marginTop: 40 }}>
        {loading ? (
          <Spin
            indicator={<LoadingOutlined style={{ fontSize: 32 }} spin />}
            tip="Evaluating Merchant Data"
            size="large"
          />
        ) : evaluated ? (
          <Result
            status="success"
            title="Evaluation Complete"
            subTitle="The merchant data has been successfully evaluated."
          />
        ) : (
          <p>No evaluation in progress.</p>
        )}
      </div>
    </div>
  );
};

export default App;
