import React, { useState, useEffect } from 'react';
import { Button, Spin, Typography, Result, Form, Input, Card } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';
import './App.css';

const { Title } = Typography;

interface MerchantData {
  merchantName: string;
  businessType: string;
  contactEmail: string;
  phoneNumber: string;
  address: string;
}

const App: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [evaluated, setEvaluated] = useState(false);
  const [polling, setPolling] = useState(false);
  const [form] = Form.useForm<MerchantData>();

  const simulateEventTrigger = (values: MerchantData) => {
    setLoading(true);
    setEvaluated(false);
    setPolling(true);

    setTimeout(() => {
      setEvaluated(true);
      setPolling(false);
      setLoading(false);
    }, 6000);
  };

  useEffect(() => {
    if (!polling) return;

    const interval = setInterval(() => {
      console.log('Polling backend...');
    }, 2000);

    return () => clearInterval(interval);
  }, [polling]);

  return (
    <div style={{ padding: '60px', maxWidth: '800px', margin: '0 auto' }}>
      <Title level={3} style={{ textAlign: 'center' }}>Merchant Evaluation</Title>

      <Card style={{ marginBottom: 24 }}>
        <div style={{ position: 'relative' }}>
          <Form
            form={form}
            layout="vertical"
            onFinish={simulateEventTrigger}
            style={{ opacity: loading ? 0.5 : 1 }}
          >
            <Form.Item
              name="merchantName"
              label="Merchant Name"
            >
              <Input placeholder="Enter merchant name" disabled={loading} />
            </Form.Item>

            <Form.Item
              name="businessType"
              label="Business Type"
            >
              <Input placeholder="Enter business type" disabled={loading} />
            </Form.Item>

            <Form.Item
              name="contactEmail"
              label="Contact Email"
            >
              <Input placeholder="Enter contact email" disabled={loading} />
            </Form.Item>

            <Form.Item
              name="phoneNumber"
              label="Phone Number"
            >
              <Input placeholder="Enter phone number" disabled={loading} />
            </Form.Item>

            <Form.Item
              name="address"
              label="Address"
            >
              <Input.TextArea placeholder="Enter address" rows={3} disabled={loading} />
            </Form.Item>

            <Form.Item>
              <Button type="primary" htmlType="submit" disabled={loading} block>
                Save & Evaluate
              </Button>
            </Form.Item>
          </Form>

          {loading && (
            <div style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              zIndex: 1000
            }}>
              <Spin
                indicator={<LoadingOutlined style={{ fontSize: 32 }} spin />}
                tip="Evaluating Updated Merchant Data"
                size="large"
              />
            </div>
          )}
        </div>
      </Card>

      <div style={{ textAlign: 'center' }}>
        {evaluated && !loading && (
          <Result
            status="success"
            title="Evaluation Complete"
            subTitle="The merchant data has been successfully evaluated."
          />
        )}
      </div>
    </div>
  );
};

export default App;
